package com.mm.struts.advt.action;

import java.io.PrintWriter;
import java.util.ArrayList;
import java.util.HashMap;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;

/** 
 * MyEclipse Struts
 * Creation date: 02-19-2008
 * 
 * XDoclet definition:
 * @struts.action validate="true"
 */
public class AdvttestcatAction extends Action {
	/*
	 * Generated Methods
	 */

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form, HttpServletRequest request, HttpServletResponse response) throws Exception 
	{
		String strCat = (request.getParameter("catname") != null) ? request.getParameter("catname") : "TechnicalSkills";
		String strCid = (request.getParameter("catid") != null) ? request.getParameter("catid") : "0";
		HttpSession session = request.getSession();
		HashMap catList = (HashMap)session.getAttribute("AllTestCategory");
		String strFinalStrCat = "";
		if(request.getParameter("catname")!=null)
		{
			strFinalStrCat = getSubCatList(Integer.parseInt(strCid.trim()), strCat, catList);
		}
		else
		{
			strFinalStrCat ="<select name=\"testsubcat\" class=\"input_combo_box\" tabindex=\"10\"  onchange=\"retrieveMytestcatURL('advttestcat.do?catname="+strCat+"&catid='+this.value);\">";
			strFinalStrCat += "<option value=\"0\" Selected>Select</option>";
			strFinalStrCat += "<option value=\"-1\">Others</option>";
			strFinalStrCat += "</select>";
		}
	    
	    response.setContentType("text/html;charset=ISO-8859-1");
	    PrintWriter out = response.getWriter();
	    out.println(strFinalStrCat);
	    out.flush();
		
		return null;
	}
	private String getSubCatList(int numCatId, String strCat, HashMap catList)
	{
		ArrayList AllStates = (ArrayList)catList.get(strCat);
		
		String strResult ="<select name=\"testsubcat\" class=\"input_combo_box\" tabindex=\"10\"  onchange=\"retrieveMytestcatURL('advttestcat.do?catname="+strCat+"&catid='+this.value);\">";
		
		for(int i=0; i<AllStates.size(); i++)
		{
			ArrayList StatesArr = (ArrayList)AllStates.get(i);
			int numStateId = Integer.parseInt(StatesArr.get(0).toString().trim());
			String strSName = StatesArr.get(1).toString().trim();
				if(numCatId == numStateId)
			{
				strResult += "<option value=\"" + numStateId + "\" Selected>" + strSName + "</option>";
			}
			else
			{
				strResult += "<option value=\"" + numStateId + "\">" + strSName + "</option>";
			}
		}
		
		strResult += "</select>";
		return strResult;
	}
}