package com.mm.struts.corpo.action;

import java.io.PrintWriter;
import java.util.Vector;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;

import com.mm.core.master.AdvtMaster;

/** 
 * MyEclipse Struts
 * Creation date: 07-16-2007
 * 
 * XDoclet definition:
 * @struts.action validate="true"
 */
public class CorpocategoryAction extends Action {
	/*
	 * Generated Methods
	 */

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) throws Exception 
	{
		int numAdvtCId = (request.getParameter("advtcat")!=null) ? Integer.parseInt(request.getParameter("advtcat").trim()) : 0;
		int numAdvtId = (request.getParameter("advtid")!=null) ? Integer.parseInt(request.getParameter("advtid").trim()) : 0;
		int numTempCount = (request.getParameter("numCount") != null) ? Integer.parseInt(request.getParameter("numCount").trim()) : 0;
		////////System.out.println("Company Id in Advt Category "+numAdvtId);
    	//.out.println("Catid Id in Advt Category "+numAdvtCId);
		AdvtMaster am = new AdvtMaster();
		Vector resultVec = am.getBrandCategory(numAdvtId, getDataSource(request, "corpo"));
		////////System.out.println("Result Vector in Advt Category "+resultVec);
		String strBrandCategory =getBrandCat(numTempCount, numAdvtCId, resultVec);
		////////System.out.println("String Brand Category in Advt Category "+strBrandCategory);
	    response.setContentType("text/html;charset=ISO-8859-1");
	    PrintWriter out = response.getWriter();
	    out.println(strBrandCategory);
	    out.flush();
		return null;
	}
	
	private String getBrandCat(int numTempCount, int numAdvtCat, Vector CategoryVec)
	{
		String strResult ="<select name=\"cmbcat\" id=\"cmbcat\"  onchange=\"CatOnChange()\">";
		Vector<String> tempVec = new Vector<String>();
		tempVec.add("0");
		tempVec.add("Select");
		CategoryVec.add(0, tempVec);
		for(int i=0; i<CategoryVec.size(); i++)
		{
			Vector tempVec1 = (Vector)CategoryVec.elementAt(i);
			int numCatId = Integer.parseInt(tempVec1.elementAt(0).toString().trim());
			String strCatName = tempVec1.elementAt(1).toString().trim();
			////////System.out.println("numCountryId "+numCountryId);
			////////System.out.println("strCName "+strCName);
			if(numCatId == numAdvtCat)
			{
				strResult += "<option value=\"" + numCatId + "\" Selected>" + strCatName + "</option>";
			}
			else
			{
				strResult += "<option value=\"" + numCatId + "\">" + strCatName + "</option>";
			}
		}
		strResult += "</select>";
		int numtotal = numTempCount/2;
		if(numtotal !=0)
		{
			strResult += "";
		}
		return strResult;
	}
}