	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		System.out.println("1111In execute Member Master Action");
		HttpSession session = request.getSession();
		MemberForm memberForm = (MemberForm) form;
		MemberDao memberDao = new MemberDao();
		DataSource dataSource = getDataSource(request, "corpo");
		String operation = request.getParameter("operation");
		
		String forward = "showMembers";
		System.out.println("33operation in Staff Master->"+operation);
		
		int facilityId = (session.getAttribute("fId")!=null) ? Integer.parseInt((String)session.getAttribute("fId")) : 0;
		System.out.println("facilityId in Member Master Action->"+facilityId);

		memberForm.setFacilityId(facilityId);
		
		//System.out.println("33operation in Staff Master->"+operation);
		//System.out.println("44operationNew->"+memberForm.getOperation());
		
		request.setAttribute("facilityId", facilityId);
		
		//int fac_id=Integer.parseInt((String)(request.getAttribute("facilityId")));
		//System.out.println("fac_id->"+fac_id);
		if (operation == null) {
			operation = memberForm.getOperation();
		}
		if (operation == null) {
			operation = "";
		}
		
		
		
		try {
			if (operation.equals("addnew")) {
				String results = validateServiceForm(memberForm);
				if (results != null) {
					request.setAttribute(APPConstant.MESSAGE, results);
					forward = "addStaffFailure";
				}
				else {
					System.out.println("Ready To Insert....................Member Master Form");
					//memberDao.insertDetail(dataSource, memberForm);
					Vector consumerList = memberDao.getMemberList(dataSource, facilityId);
					System.out.println("consumerList in Member Master Action->"+consumerList);
					request.setAttribute(APPConstant.MEMBER_LIST, consumerList);
					request.setAttribute(APPConstant.MESSAGE, APPConstant.INSERT_SUCCESS);
					forward = "showMembers";
				}
			} else if (operation.equals("edit")) {
				memberDao.updateMemberDetail(dataSource, memberForm);
				Vector consumerList = memberDao.getMemberList(dataSource, facilityId);
				request.setAttribute(APPConstant.MEMBER_LIST, consumerList);
				request.setAttribute(APPConstant.MESSAGE, APPConstant.EDIT_SUCCESS);
				forward = "showMembers"; 
			}
			else if (operation.equals("forwardToAddPage")) {
				System.out.println("In Member Master Action for adding->"+forward);
				forward = "addMember";
			} else if (operation.equals("forwardToEditPage")) {
				System.out.println("setDataForEditPage->"+operation);
				setDataForEditPage(request);
				forward = "editMembers";
			}else if (operation.equals("forwardToPrintPage")) {
				System.out.println("setDataForPrintPage->"+operation);
				setDataForPrintPage(request);
				forward = "printMembers";	
			} else if (operation.equals("delete")) {
				deleteStaff(request, facilityId);
			} else if (operation.equals("showMembers")) {
				Vector memberList = memberDao.getMemberList(dataSource, facilityId);
				System.out.println("getMemberList in Member Manage Action->"+memberList);
				request.setAttribute(APPConstant.MEMBER_LIST, memberList);
			}else if (operation.equals("searchService")){
				String serviceName=request.getParameter("serviceName"); 
				Vector consumerList = memberDao.searchServices(dataSource, serviceName);
				request.setAttribute(APPConstant.MEMBER_LIST, consumerList);
			}else if (operation.equals("showServiceResident")){
				int consumerId = getConsumerId(session);
				//int facilityId = getFacilityId(session);
				String serviceName=request.getParameter("serviceName"); 
				Vector consumerList = memberDao.getServiceListResident(dataSource, consumerId, facilityId);
				request.setAttribute(APPConstant.MEMBER_LIST, consumerList);
				forward = "showServiceResident";
			}
		} catch (Exception exception) {
			exception.printStackTrace();
			request.setAttribute(APPConstant.MESSAGE,APPConstant.FAILURE_MESSAGE);
		}
		System.out.println("Return From Staff Manage Action in Initial ->"+forward);
		return mapping.findForward(forward);
	}


	private void deleteStaff(HttpServletRequest request, int facilityId)
			throws Exception {
		DataSource dataSource = getDataSource(request, "corpo");
		String staffIds = request.getParameter("staffIds");
		String staffIdsArr[] = staffIds.split(",");
		
		MemberDao memberDao = new MemberDao();
		memberDao.inactiveStaff(dataSource, staffIdsArr);
		//Vector staffList = memberDao.getStaffList(dataSource, facilityId);
		//request.setAttribute(APPConstant.MEMBER_LIST, staffList);
		//request.setAttribute(APPConstant.MESSAGE, APPConstant.DELETE_SUCCESS);
	}

	private void setDataForEditPage(HttpServletRequest request)	throws Exception {
		MemberDao memberDao = new MemberDao();
		DataSource dataSource = getDataSource(request, "corpo");
		int userId = Integer.parseInt((String)request.getParameter("userId"));
		System.out.println("In Staff Master Action-->"+userId);
		MemberBean bean = memberDao.getStaffData(dataSource, userId);
		System.out.println("bean Returned In Staff Master Action-->"+bean);
		request.setAttribute(APPConstant.MEMBERDATA, bean);
	}
	
	private void setDataForPrintPage(HttpServletRequest request)	throws Exception {
		MemberDao memberDao = new MemberDao();
		DataSource dataSource = getDataSource(request, "corpo");
		int userId = Integer.parseInt((String)request.getParameter("userId"));
		System.out.println("In Staff Master Action-->"+userId);
		MemberBean bean = memberDao.getStaffData(dataSource, userId);
		System.out.println("bean Returned In Staff Master Action-->"+bean);
		request.setAttribute(APPConstant.MEMBERDATAPRINT, bean);
	}
	
	private String validateServiceForm(MemberForm memberForm ) {
		
		if (memberForm == null) {
			return "Please fill New Staff form";
		}
		else if (memberForm.getStaffName() == null ||  memberForm.getStaffName().trim().length() <= 0) {
				return "Staff Name cannot be empty";
			}
		return null;
		
	}
	
	
	/**
	 * date help function
	 * @param str_date
	 * @return
	 * @throws Exception
	 */
	private Date getDate(String str_date) throws Exception {
		DateFormat formatter = new SimpleDateFormat("dd/MM/yyyy");
		Date sqlDate = new java.sql.Date(formatter.parse(str_date).getTime());
		return sqlDate;
	}
	
	/**
	 * Date Help function
	 * @param month
	 * @param year
	 * @return
	 * @throws Exception
	 */
	private Date getDate(int month,int year) throws Exception {
		String billMonth = "";
		if(month<10){
			billMonth = "0"+month;
		}else{
			billMonth = String.valueOf(month);
		}
		String str_date = "01/"+billMonth+"/"+year;
		DateFormat formatter = new SimpleDateFormat("dd/MM/yyyy");
		Date sqlDate = new java.sql.Date(formatter.parse(str_date).getTime());
		return sqlDate;
	}
	
	public int getFacilityId(HttpSession session) {
		if(session.getAttribute(APPConstant.USER_INFORMATION) == null){
			return 1;
		}
		LoginHelper helper = (LoginHelper) session
				.getAttribute(APPConstant.USER_INFORMATION);
		return helper.getFacilityId();
	}
	public int getConsumerId(HttpSession session) {
		if(session.getAttribute(APPConstant.USER_INFORMATION) == null){
			return -1;
		}
		LoginHelper helper = (LoginHelper) session
				.getAttribute(APPConstant.USER_INFORMATION);
		return helper.getConsumerId();
	}
	
	public String getLoginName(HttpSession session) {
		if(session.getAttribute(APPConstant.USER_INFORMATION) == null){
			return "";
		}
		LoginHelper helper = (LoginHelper) session
				.getAttribute(APPConstant.USER_INFORMATION);
		return helper.getUserName();
	}
