	private String getSubCat(int numAdvtCat, Vector CategoryVec, int[] numSubCatId, int numTempCount)
	{
		String strDisabled = (numSubCatId[12]==2 || numSubCatId[12]==3) ? "disabled=\"true\"" : "";
		String strResult ="<select name=\"cmbcat\" id=\"cmbcat\" class=\"input12\" onchange=\"retrieveUserGrp('userrightsgroup.do?pgstate="+numSubCatId[12]+"&numCount="+numTempCount+"&catid='+this.value+'&catid1="+numSubCatId[0]+"&catid2="+numSubCatId[2]+"&catid3="+numSubCatId[3]+"&catid4="+numSubCatId[4]+"&catid5="+numSubCatId[5]+"&catid6="+numSubCatId[6]+"&catid7="+numSubCatId[7]+"&catid8="+numSubCatId[8]+"&catid9="+numSubCatId[9]+"&catid10="+numSubCatId[10]+"&compId="+numSubCatId[11]+"');\""+strDisabled+">";
		Vector<String> tempVec = new Vector<String>();
		tempVec.add("0");
		tempVec.add("Select");
		CategoryVec.add(0, tempVec);
		for(int i=0; i<CategoryVec.size(); i++)
		{
			Vector tempVec1 = (Vector)CategoryVec.elementAt(i);
			int numCatId = Integer.parseInt(tempVec1.elementAt(0).toString().trim());
			String strCatName = tempVec1.elementAt(1).toString().trim();
			//////////System.out.println("numCountryId "+numCountryId);
			//////////System.out.println("strCName "+strCName);
			if(numCatId == numAdvtCat)
			{
				strResult += "<option value=\"" + numCatId+"\" Selected>" + strCatName + "</option>";
			}
			else
			{
				strResult += "<option value=\"" + numCatId+"\">" + strCatName + "</option>";
			}
		}
		strResult += "</select>";
		return strResult;
	}
