package com.mm.struts.entp.action;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import org.apache.struts.action.Action;
import org.apache.struts.action.ActionError;
import org.apache.struts.action.ActionErrors;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import com.mm.core.master.EntpMaster;
import java.util.Vector;

/** 
 * MyEclipse Struts
 * Creation date: 07-27-2007
 * 
 * XDoclet definition:
 * @struts.action validate="true"
 */

public class ChangePublishAction extends Action {
	/*
	 * Generated Methods
	 */

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		EntpMaster entpMaster = new EntpMaster();
		String complanitId = request.getParameter("complainId");
		String publish = request.getParameter("publish");
		//System.out.println("complanitId in change publish action>>>>>>>>>>>>>"+complanitId);
		//System.out.println("publish in change publish action>>>>>>>>>>>>>"+publish);
		Vector<String> dataVec = new Vector<String>();
		dataVec.add(complanitId);
		dataVec.add(publish);
		String status = entpMaster.changepublish(getDataSource(request, "entp"), dataVec);
		if(status.equalsIgnoreCase("success"))
		{
			ActionErrors errors = new ActionErrors();
			errors.clear();
			errors.add("publish", new ActionError("errors.publish.success"));
			
				saveErrors(request, errors);
		}
		else
		{
			ActionErrors errors = new ActionErrors();
			errors.clear();
			errors.add("publish", new ActionError("errors.publish.failure"));
			
				saveErrors(request, errors);
		}
		
		return mapping.findForward(status);
	}
}