package com.mm.struts.entp.action;


import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;
import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import com.mm.core.master.EntpMaster;

import java.util.Vector;
/** 
 * MyEclipse Struts
 * Creation date: 07-27-2007
 * 
 * XDoclet definition:
 * @struts.action validate="true"
 */
public class GetCommentAction extends Action {
	/*
	 * Generated Methods
	 */

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		EntpMaster entpMaster = new EntpMaster();
		
		HttpSession session = request.getSession();
		String commentId = (request.getParameter("commentId")!=null)? request.getParameter("commentId") : (String)session.getAttribute("commentId");
		Vector commentfVec = entpMaster.getCommentDetails(getDataSource(request, "entp"), Integer.parseInt(commentId));
		request.setAttribute("commentfVec",commentfVec);
		//System.out.println("commentfVec>>>>>>>>>>>>>>>>"+commentfVec);
		return mapping.findForward("success");
	}
}