package com.mm.struts.indv.action;

import java.io.PrintWriter;
import java.util.Vector;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import com.mm.core.master.IndvMaster;

/** 
 * MyEclipse Struts
 * Creation date: 06-30-2007
 * 
 * XDoclet definition:
 * @struts.action validate="true"
 */
public class ContactPersonAction extends Action {
	
	/*
	 * Generated Methods
	 */

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) throws Exception {
		IndvMaster indvMaster= new IndvMaster();
		int numCatId = (request.getParameter("category")!=null) ? Integer.parseInt(request.getParameter("category").trim()) : 0;
		int numBrandId = (request.getParameter("brand")!=null) ? Integer.parseInt(request.getParameter("brand").trim()) : 0;
		int numContId = (request.getParameter("contact")!=null) ? Integer.parseInt(request.getParameter("contact").trim()) : 0;
		//System.out.println("numCatId>>>>>>>>>>"+numCatId);
		//System.out.println("numBrandId>>>>>>>>>>"+numBrandId);
		//System.out.println("numContId>>>>>>>>>>"+numContId);
		String strContact = "";
		if(request.getParameter("brand")!=null)
		{
			
			Vector contactVec = indvMaster.getContactPerson(numCatId, numBrandId, getDataSource(request, "indv"));
			//System.out.println("contactVec>>>>>>>>>>>>"+contactVec);
			strContact = getContactPerson(numContId, contactVec);
		}
		else
		{
			strContact ="<select name=\"contact\" class=\"input_multi_txt_box\" tabindex=\"4\" onchange=\"setContact()\">";
			strContact += "<option value=\"0\" Selected>Select</option>";
			strContact += "<option value=\"-1\">Others</option>";
			strContact += "</select>";
			
		}
		//System.out.println("strContact>>>>>>>>>>>>"+strContact);
	    response.setContentType("text/html;charset=ISO-8859-1");
	    PrintWriter out = response.getWriter();
	    out.println(strContact);
	    out.flush();
		return null;
	}
	
	private String getContactPerson(int numContId, Vector brandVec)
	{
		String strResult ="<select name=\"contact\" class=\"input_multi_txt_box\" tabindex=\"4\" onchange=\"setContact()\">";
		
		for(int i=0; i<brandVec.size(); i++)
		{
			Vector tempVec = (Vector)brandVec.elementAt(i);
			int numContactId = Integer.parseInt(tempVec.elementAt(0).toString().trim());
			String strContactName = tempVec.elementAt(1).toString().trim() +" "+tempVec.elementAt(2).toString().trim();
			//System.out.println("numPlaceId "+numPlaceId);
			//System.out.println("strPName "+strPName);
			if(numContId == numContactId)
			{
				strResult += "<option value=\"" + numContactId + "\" Selected>" + strContactName + "</option>";
			}
			else
			{
				strResult += "<option value=\"" + numContactId + "\">" + strContactName + "</option>";
			}
		}
		strResult += "</select>";
		return strResult;
	}
}