	private String getBrnadList(DataSource ds, String companyName,String[] arrCat, int numCid ,String detailFor, Vector contactVec, int brandId, String cuid, int numTempCount)
	{	
		
		RootMaster rootMaster= new RootMaster();
		
		String strResult = "";
		
		strResult +="<table width=\"100%\" >";
		strResult +="<tr >";
		strResult +="<td width=\"25%\" height=\"30\" align=\"right\" class=\"bold\" >Name&nbsp;&nbsp;</td>";
		strResult +="<td height=\"30\" align=\"left\" >"+companyName+"</td>";
		strResult +="</tr>";
		
		strResult +="<tr >";
		strResult +="<td width=\"25%\" height=\"30\" align=\"right\" class=\"bold\" >Category&nbsp;&nbsp;</td>";
		strResult +="<td height=\"30\" align=\"left\" >";
		strResult +="<select name=\"category\"  tabindex=\"2\" >";
		
		String catName = "";
			
		for(int i=1; i<arrCat.length; i++)
		{
			
			int numCatId = Integer.parseInt(arrCat[i].toString().trim());
			
			
			if(detailFor.equalsIgnoreCase("Corporates")||detailFor.equalsIgnoreCase("company")){
				
				Vector strCName = rootMaster.getCategoryName(ds, numCatId);
				catName = strCName.elementAt(0).toString();
				
			}else {
					
				 //System.out.println("numCatId...else............."+numCatId);
				 catName = rootMaster.getStrCollegeCategoryName(ds, numCatId);
				 //System.out.println("catName...else............."+catName);
			}
			
			
			if(numCid == numCatId)
			{
				strResult += "<option value=\"" + numCatId + "\" Selected>" + catName + "</option>";
			}
			else
			{
				strResult += "<option value=\"" + numCatId + "\">" + catName + "</option>";
			}
		}
		strResult += "</select>";
		
		strResult +="</td>";
		strResult +="</tr>";
		
		
		
		strResult +="<tr >";
		strResult +="<td width=\"25%\" height=\"30\" align=\"right\" class=\"bold\" >Contact Persion&nbsp;&nbsp;</td>";
		strResult +="<td height=\"30\" align=\"left\" >";
		
		 strResult +="<select name=\"contactPersion\"  tabindex=\"3\"  onchange=\" retrieveUserShortProURL('shortPro.do?compid='+this.value+'&pType=user')\" >";
			for(int i=0; i<contactVec.size(); i++)
			{
				Vector tempVec = (Vector)contactVec.elementAt(i);
				int numBrandId = Integer.parseInt(tempVec.elementAt(0).toString().trim());
				String strfName = tempVec.elementAt(1).toString().trim();
				String strlName = tempVec.elementAt(2).toString().trim();
				
				//////System.out.println("numPlaceId "+numPlaceId);
				//////System.out.println("strPName "+strPName);
				if(brandId == numBrandId)
				{
					strResult += "<option value=\"" + numBrandId + "\" Selected>" + strfName + " " + strlName + "</option>";
				}
				else
				{
					strResult += "<option value=\"" + numBrandId + "\" >" + strfName + " " + strlName + "</option>";
				}
			}
			strResult += "</select>";
		
		
		
		strResult +="</td>";
		strResult +="</tr>";
		
		if(numTempCount%2==1){
			
			strResult +="<tr style=\"display:none\">";
			strResult +="<td width=\"25%\" height=\"30\" align=\"right\" class=\"bold\" >&nbsp;</td>";
			strResult +="<td height=\"30\" align=\"left\" >&nbsp;</td>";
			strResult +="</tr>";
			
		}
		
		
		strResult +="</table >";
		
		
		
		
		return strResult;
	}
	
	
	
