//	validatin for the Write Complaint form. it check other category,brand and contact person.
	//and also check for the country ,state ,city others field 
	public ActionErrors validate(ActionMapping mapping, HttpServletRequest req)
	{
		ActionErrors errors = super.validate(mapping,req);
		
		if(this.country.equals("-1~+0") && this.othercountry.length()<=0)
		{
			errors.add("stdConsumerMyProfileModifyForm",new ActionError("errors.resource.other","country"));
			if(this.otherstate.length()<=0)
			{
				errors.add("stdConsumerMyProfileModifyForm",new ActionError("errors.resource.other","state"));
				if(this.othercity.length()<=0)
				{
					errors.add("stdConsumerMyProfileModifyForm",new ActionError("errors.resource.other","city"));
				}
			}
		}
		else
		{
			if(this.state.equals("-1") && this.otherstate.length()<=0)
			{
				errors.add("stdConsumerMyProfileModifyForm",new ActionError("errors.resource.other","state"));
				if(this.othercity.length()<=0)
				{
					errors.add("stdConsumerMyProfileModifyForm",new ActionError("errors.resource.other","city"));
				}
			}
			else
			{
				if(this.city.equals("-1") && this.othercity.length()<=0)
				{
					errors.add("stdConsumerMyProfileModifyForm",new ActionError("errors.resource.other","city"));
				}
			}
			
		}
		if(this.txtPrefix.equals("-1"))
		{
			errors.add("stdConsumerMyProfileModifyForm",new ActionError("errors.resource.valid","mobile prefix"));
		}
		
			
		return errors;
	}
